import React, {Component} from 'react';
import logo from './logo.svg';
import './App.css';
import Spotify from 'spotify-web-api-js'

const spotifyWebApi = new Spotify();

class App extends Component {
  constructor(props) {
    super(props);
    const params = this.getHashParams();

    this.state = {
        loggedIn : params.access_token ? true : false,
        artists : [], // just artists
        artistAlbums : [], // artists and their albums
    }

    if (params.access_token) {
        spotifyWebApi.setAccessToken(params.access_token);
    }

  }

  componentDidMount() {
      if (this.state.loggedIn) {
          spotifyWebApi.getFollowedArtists({limit:15})
          .then((response) => { // waits for artists followed
              this.setState({
                  artists : response.artists.items
              });
              return response.artists.items.map((artist) => {return artist.id});
          })
          .then((artistIds) => { // waits for artist id array
              const artistAlbums = artistIds.map((artistId) => {
                  return spotifyWebApi.getArtistAlbums(artistId);
              });
              return Promise.all(artistAlbums); // this resolves the promise for each album request
          })
          .then((albums) => {
              this.setState({
                  artistAlbums: albums
              });
          });
      }
  }

  getHashParams() { // provided by jmperez
    var hashParams = {};
    var e, r = /([^&;=]+)=?([^&;]*)/g,
        q = window.location.hash.substring(1);
    while ( e = r.exec(q)) {
       hashParams[e[1]] = decodeURIComponent(e[2]);
    }
    return hashParams;
  }

  render() {
      //const artistsFollowed = this.state.artists;

    const artists = this.state.artists;
    const artistList = artists.map(artist => {
        return (
            <div key={artist.id} style={{
            border:'3px solid black',
            margin: '4px',
            padding: '5px'
            }}>
                {artist.name}
            </div>)
    });

      return (
        <div className="App">
          <a href="http://localhost:8888">
            <button>Login to Spotify</button>
          </a>
          <div>
              {artistList}
          </div>
        </div>
      );
  }
}

export default App;
