import React, {Component} from 'react';
import './App.css';
import Spotify from 'spotify-web-api-js'
import ReleaseCard from './components/ReleaseCard.js'

const spotifyWebApi = new Spotify();
const calendar = ['January', 'February', 'March', 'April', 'May', 'June', 'July', 'August', 'September', 'October', 'November', 'December'];
const today = new Date();

class App extends Component {
  constructor(props) {
    super(props);
    const params = this.getHashParams();

    this.state = {
        loggedIn : params.access_token ? true : false,
        artists : [], // just artists
        artistAlbums : [], // albums grouped by artist,
        artistReleases : [],
        userDataReceived : false, // used to control rendering,
        displayName: "",
        profileImage: ""
    }

    if (params.access_token) {
        spotifyWebApi.setAccessToken(params.access_token);
    }

  }

  componentDidMount() {
      if (this.state.loggedIn) {
          spotifyWebApi.getMe().then((response) => {this.setState({displayName: response.display_name, profileImage: response.images[0].url})})

          spotifyWebApi.getFollowedArtists({limit:5}) // WHAT IF YOU HAVE MORE THAN 50 FOLLOWED ARTISTS?
          .then((response) => { // waits for artists followed
              this.setState({
                  artists : response.artists.items
              });
              return response.artists.items.map((artist) => {return {name : artist.name, id : artist.id}});
          })
          .then((artists) => { // waits for artist id array
              const artistAlbums = artists.map((artist) => {
                  return spotifyWebApi.getArtistAlbums(artist.id, {limit:50, country:'US', include_groups: 'album,single'})
                  .then((albums) => {
                      return (albums.items);
                  })
              });
              return Promise.all(artistAlbums);
          })
          .then((albums) => {
              this.setState({
                  artistReleases : albums.flat().sort((a,b) => a.release_date < b.release_date ? 1 : -1), // hack for chrome functionality
                  artistAlbums: albums,
                  userDataReceived : true
              });
          });
      }
  }

  getHashParams() { // provided by jmperez
    var hashParams = {};
    var e, r = /([^&;=]+)=?([^&;]*)/g,
        q = window.location.hash.substring(1);
    while ( e = r.exec(q)) {
       hashParams[e[1]] = decodeURIComponent(e[2]);
    }
    return hashParams;
  }

  render() {
      //const artistsFollowed = this.state.artists;

    const artistReleases = this.state.artistReleases;
    const releaseList = artistReleases.map((release)=> {
        /*const releaseDate = new Date(release.release_date);

        var displayedDate = '';
        if (releaseDate.getFullYear() == today.getFullYear()) {
            displayedDate = displayedDate.concat(calendar[releaseDate.getMonth()]);
            displayedDate = displayedDate.concat(' ' + releaseDate.getDate() + ', ');
        }
        displayedDate = displayedDate.concat(releaseDate.getFullYear());

        const tracks = [];
        spotifyWebApi.getAlbum(release.id)
        .then((fullRelease) => {
            tracks.push(fullRelease.tracks.items);
            console.log(tracks);
        });*/

        //console.log(tracks);





        /*const trackList = tracks.map((track) => {
            return (
                <div>hi</div>
            );
        })*/

        return (
            /*<div key={release.id} className="release-card">
                <div><img src={release.images[0].url} height="200" onClick={() => spotifyWebApi.play({context_uri : release.uri})}></img></div>
                <div className="release-card-body">
                    <div>{displayedDate}</div>
                    <h2>{release.name}</h2>
                    <h3>by {release.artists[0].name}</h3>
                </div>
                <div className="release-playbar">{trackList}</div>
            </div>*/
            <ReleaseCard releaseInfo={release}/>


        )
    })

      return (
        <div className="App">
            <header className="stickyHeader">
                <h1 className="header-brand">My Music Timeline</h1>
                <div>INSERT NOW PLAYING SECTION</div>
                <div className="header-profile">
                    <div className="displayName">{this.state.displayName}</div>
                    <div className="imageCropper"><img src={this.state.profileImage} height="50px"/></div>
                </div>
            </header>
            <div className="body">
                {!this.state.loggedIn &&
                    <a href="http://localhost:8888">
                        <button style={{background: '#1DB954'}}>Login to Spotify</button>
                    </a>
                }
                {releaseList}
            </div>
        </div>
      );
  }
}

export default App;
