import React, {Component} from 'react';
import logo from './logo.svg';
import './App.css';
import Spotify from 'spotify-web-api-js'

const spotifyWebApi = new Spotify();

class App extends Component {
  constructor(props) {
    super(props);
    const params = this.getHashParams();

    this.state = {
        loggedIn : params.access_token ? true : false,
        artists : [], // just artists
        artistAlbums : [], // albums grouped by artist,
        artistRelease : [],
        userDataReceived : false // used to control rendering
    }

    if (params.access_token) {
        spotifyWebApi.setAccessToken(params.access_token);
    }

  }

  componentDidMount() {
      if (this.state.loggedIn) {
          spotifyWebApi.getFollowedArtists({limit:50})
          .then((response) => { // waits for artists followed
              this.setState({
                  artists : response.artists.items
              });
              return response.artists.items.map((artist) => {return {name : artist.name, id : artist.id}});
          })
          .then((artists) => { // waits for artist id array
              const artistAlbums = artists.map((artist) => {
                  return spotifyWebApi.getArtistAlbums(artist.id, {limit:10, country:'US'});
              });
              return Promise.all(artistAlbums); // this resolves the promise for each album request
          })
          .then((albums) => {
              console.log(albums);
              this.setState({
                  artistAlbums: albums,
                  userDataReceived : true
              });
          });
      }
  }

  getHashParams() { // provided by jmperez
    var hashParams = {};
    var e, r = /([^&;=]+)=?([^&;]*)/g,
        q = window.location.hash.substring(1);
    while ( e = r.exec(q)) {
       hashParams[e[1]] = decodeURIComponent(e[2]);
    }
    return hashParams;
  }

  render() {
      //const artistsFollowed = this.state.artists;

    const artists = this.state.artists;
    const albums = this.state.artistAlbums;
    const artistList = albums.map((catalog, i) => {
        console.log(catalog);
        const albums = catalog.items.map((album) => {
            return <div>{album.name}</div>
        })

        return (
            <div key={artists[i].id} style={{
            border:'3px solid black',
            margin: '4px',
            padding: '5px'
            }}>
                <b>{artists[i].name}</b>
                <ol>
                    {albums}
                </ol>
            </div>)
    });

      return (
        <div className="App">
          <a href="http://localhost:8888">
            <button>Login to Spotify</button>
          </a>
          <div>
              {artistList}
          </div>
        </div>
      );
  }
}

export default App;
